package eaxm02;

public class 증감연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10, b = 15;
		
		a++; // a = 11
		a++; // a = 12
		
		System.out.println(a);
		System.out.println(++a);  // +1 해주고 a 처리
		System.out.println(a++);  // a 처리를 해 주고 +1
		System.out.println(a);
		System.out.println(a++ + b);
		System.out.println(a);
				
		
		
	}

}
